const buildCart = properties => {
  const {
    variantSelector
  } = nodes;
  let selectedVariant = variantSelector[0];

  if (variantSelector.length > 1) {
    variantSelector.forEach(variant => {
      if (variant.checked) {
        selectedVariant = variant;
      }
    });
  }

  const {
    value: variantId,
    dataset: {
      sku,
      productHandle
    }
  } = selectedVariant;
  window.Shopify.queue.push({
    variantId,
    quantity: 1,
    properties
  }); // Free TPU with certain cases

  if (window.theme.caseSkusTPU[sku]) {
    properties['_bundledVariantId'] = window.theme.caseSkusTPU[sku];
    window.Shopify.queue.push({
      variantId: window.theme.caseSkusTPU[sku],
      quantity: 1
    });
  } // Wireless charger with stand bundle


  if (window.theme.wirelessChargerBundle.handles.includes(productHandle)) {
    window.theme.wirelessChargerBundle.handles.forEach(handle => {
      if (handle === productHandle) {
        properties['_bundledVariantId'] = window.theme.wirelessChargerBundle.standId;
        window.Shopify.queue.push({
          variantId: window.theme.wirelessChargerBundle.standId,
          quantity: 1,
          properties: {
            _isHiddenBundledItem: true
          }
        });
      }
    });
  }

  dataLayerAddCart(window.productVariants[variantId]);
};
