/**
 * Retrieve the selected gallery media (images & video).
 */
const getSelectedGalleryMedia = variantId => {
  return new Promise(resolve => {
    const {
      media: galleryMedia
    } = window.productVariants[variantId];
    const media = {
      main: [],
      nav: []
    };
    let mediaAlt = '';
    let slideCount = 0; // Don't run if product has no media.

    if (!galleryMedia.length) {
      return;
    } // Set the current height of the container so it doesn't jump when switching slide.


    nodes.galleryContainer.style.height = `${nodes.galleryContainer.offsetHeight}px`;
    nodes.galleryContainer.classList.remove(classes.isLoaded);
    galleryMedia.forEach(slide => {
      const isVideoSlide = slide.type === 'video';
      let mediaTagHtml;

      if (!slide.alt.includes('list-image')) {
        // Remove the SKU from the alt tag and trim the whitespace.
        if (slide.alt.includes('|')) {
          mediaAlt = slide.alt.slice(0, slide.alt.indexOf('|')).trim();
        } else {
          mediaAlt = slide.alt;
        } // determine whether to output img or video tag


        if (isVideoSlide) {
          mediaTagHtml = `
            <video
              src="${slide.main}"
              aria-label="${mediaAlt}"
              pos="video-slide"
              autoplay
              loop
              muted
              playsinline
            ></video>`;
        } else {
          mediaTagHtml = `
            <img
              src="${slide.main}"
              alt="${mediaAlt}"
            >`;
        }

        const mediaMainHtml = `
          <div class="pos-gallery-carousel__item" pos="gallery-carousel-cell">
            ${mediaTagHtml}
          </div>
        `;
        media.main.push(mediaMainHtml);
        const mediaNavHtml = `
          <div
            class="pos-gallery-nav__item${slideCount === 0 ? ' is-active' : ''}"
            pos="gallery-nav-cell"
            data-index="${slideCount}"
          >
            ${isVideoSlide ? '<div class="pos-gallery-nav__video-thumb"></div>' : mediaTagHtml}
          </div>
        `;
        media.nav.push(mediaNavHtml);
        slideCount++;
      }
    });
    resolve(media);
  });
};
