const getSelectedProductTags = variantId => {
  const {
    offers,
    comparePence,
    pricePence
  } = window.productVariants[variantId];
  const offersHtml = [];
  const percentText = nodes.tagsContainer.dataset.percentText;
  return new Promise(resolve => {
    offers.forEach(offer => {
      let offer_class = 'dark';

      if (offer.includes('magsafe')) {
        offer_class = 'light';
      } else {
        offer_class = 'dark';
      }

      offersHtml.push(`<div class="pos-gallery__tag pos-gallery__tag--${offer_class}">${offer}</div>`);
    });

    if (Number(comparePence) > Number(pricePence)) {
      const percentOff = Math.round((comparePence - pricePence) * 100 / comparePence);
      const percentTag = percentText.replace('{{percent}}', percentOff);
      offersHtml.push(`<div class="pos-gallery__tag pos-gallery__tag--percent">${percentTag}</div>`);
    }

    resolve(offersHtml);
  });
};
