const setCtaAttributes = variantId => {
  let ctaText = '';
  let hidePrice = false;
  let isCtaDisabled = false;
  let isPreorder = false;
  let isOos = false;
  let isComingSoon = false;
  nodes.ctaBtns.forEach(btn => {
    btn.removeAttribute('data-preorder');
    btn.classList.remove(classes.isPreorder);
    btn.querySelector(selectors.priceWrapper).classList.remove(classes.displayNone);
  });
  nodes.posIcons.classList.remove(classes.isPreorder);
  nodes.backInStockBtn.classList.add(classes.displayNone);
  nodes.comingSoonBtn.classList.add(classes.displayNone);

  switch (window.productVariants[variantId].variantState) {
    case 'state_instock':
      ctaText = window.theme.strings.addToCart;
      break;

    case 'state_preorder':
      ctaText = window.theme.strings.preOrder;
      isComingSoon = true;
      isPreorder = true;
      break;

    case 'state_oos':
      ctaText = window.theme.strings.outOfStock;
      isCtaDisabled = true;
      isOos = true;
      break;

    case 'state_discontinued':
      ctaText = window.theme.strings.outOfStock;
      isCtaDisabled = true;
      break;

    case 'state_coming_soon':
      ctaText = window.theme.strings.comingSoon;
      hidePrice = true;
      isCtaDisabled = true;
      isComingSoon = true;
      break;

    default:
      ctaText = window.theme.strings.addToCart;
  }

  nodes.ctaBtns.forEach(btn => {
    btn.querySelector(selectors.ctaBtnsText).innerText = ctaText;
    btn.disabled = isCtaDisabled;

    if (isPreorder) {
      btn.setAttribute('data-preorder', '');
      btn.classList.add(classes.isPreorder);
    }

    if (hidePrice && window.productVariants[variantId].pricePence === '0') {
      btn.querySelector(selectors.priceWrapper).classList.add(classes.displayNone);
    }
  });
  isOos && nodes.backInStockBtn.classList.remove(classes.displayNone);
  isComingSoon && nodes.comingSoonBtn.classList.remove(classes.displayNone);

  if (isPreorder) {
    nodes.posIcons.classList.add(classes.isPreorder);
    nodes.posIcons.querySelector(selectors.preorderDate).innerHTML = window.productVariants[variantId].preorderDate;
  }
};
